// 接收JSON格式的请求体（包含base64编码的图片）
const body = msg.payload || {};
const name = body.name;
const imageData = body.file; // base64字符串

console.log('Register request:', { name, hasFile: !!imageData });

if (!name) {
    msg.statusCode = 400;
    msg.payload = { message: "缺少必要参数: name" };
    return msg;
}

if (!imageData) {
    msg.statusCode = 400;
    msg.payload = { message: "缺少必要参数: file (base64)" };
    return msg;
}

// 生成用户ID
const userId = 'user_' + Date.now();

// 构建完整的data URL
let avatarUrl = imageData;
if (!imageData.startsWith('data:')) {
    avatarUrl = `data:image/jpeg;base64,${imageData}`;
}

console.log('注册用户:', name, '头像长度:', imageData.length);

msg.payload = {
    id: userId,
    name: name,
    avatarUrl: avatarUrl,
    registeredAt: Date.now()
};

return msg;

