return msg;

const deviceTokens = context.global.get('deviceTokens');
const devices = msg.payload;

if (!deviceTokens || !devices) return null;

console.log('[INIT] Initializing all devices on ThingsBoard...');

const messages = devices.map(device => {
    const token = deviceTokens[device.id];
    if (!token) return null;

    let attributes = { 
        isOn: device.isOn, 
        active: device.isOn, // 初始化时根据状态设置活跃标志
        name: device.name,
        type: device.type
    };

    // Add device specific attributes
    if (device.value !== undefined) {
        attributes.value = device.value;
        if (device.id === 'light-main') attributes.brightness = device.value;
        if (device.id === 'ac') attributes.temperature = device.value;
        if (device.id === 'curtain') attributes.openPercentage = device.value;
        if (device.id === 'desk') attributes.height = device.value;
    }

    return {
        url: `http://thingsboard:9090/api/v1/${token}/attributes`,
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        payload: attributes
    };
}).filter(m => m !== null);

// Also send telemetry to mark them as active
const telemetries = devices.map(device => {
    const token = deviceTokens[device.id];
    if (!token) return null;

    return {
        url: `http://thingsboard:9090/api/v1/${token}/telemetry`,
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        payload: { 
            status: device.isOn ? 'ON' : 'OFF', 
            active: device.isOn,
            lastSeen: Date.now()
        }
    };
}).filter(m => m !== null);

return [...messages, ...telemetries];

const deviceId = msg.req.params.id;
const isOn = msg.payload?.isOn !== undefined ? msg.payload.isOn : true;

const deviceTokens = context.global.get('deviceTokens');
const deviceToken = deviceTokens ? deviceTokens[deviceId] : null;

if (!deviceToken) {
    console.error('[POST /toggle] ERROR: Unknown device:', deviceId);
    msg.statusCode = 404;
    msg.payload = { message: 'Device not found' };
    return msg;
}

console.log('========== TOGGLE DEVICE ==========');
console.log('[POST /toggle] Device ID:', deviceId);
console.log('[POST /toggle] New state:', isOn ? 'ON' : 'OFF');

const httpMsg = { payload: { id: deviceId, isOn: isOn } };
const mqttMsg = {
    payload: { deviceId: deviceId, isOn: isOn },
    topic: 'iot/room/devices'
};
const tbMsg = {
    url: `http://thingsboard:9090/api/v1/${deviceToken}/attributes`,
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    payload: { isOn: isOn, active: isOn },
    deviceToken: deviceToken
};

// 同时发送遥测数据使设备变为活动状态
const tbTelemetry = {
    url: `http://thingsboard:9090/api/v1/${deviceToken}/telemetry`,
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    payload: { status: isOn ? 'ON' : 'OFF', active: isOn, timestamp: Date.now() },
    deviceToken: deviceToken
};

console.log('[POST /toggle] → MQTT Frontend:', JSON.stringify(mqttMsg.payload));
console.log('[POST /toggle] → ThingsBoard Attributes:', tbMsg.url);
console.log('[POST /toggle] → ThingsBoard Telemetry:', tbTelemetry.url);
console.log('[POST /toggle] =================================');

return [httpMsg, mqttMsg, tbMsg, tbTelemetry];

const deviceId = msg.req.params.id;
const value = msg.payload?.value;

if (value === undefined || value === null) {
    console.error('[PUT /value] ERROR: Missing value');
    msg.statusCode = 400;
    msg.payload = { message: 'Missing value' };
    return msg;
}

const deviceTokens = context.global.get('deviceTokens');
const deviceToken = deviceTokens ? deviceTokens[deviceId] : null;

if (!deviceToken) {
    console.error('[PUT /value] ERROR: Unknown device:', deviceId);
    msg.statusCode = 404;
    msg.payload = { message: 'Device not found' };
    return msg;
}

let attributes = {};
let telemetry = { active: true, timestamp: Date.now() };

if (typeof value === 'boolean') {
    attributes = { isOn: value, active: value };
    telemetry.isOn = value;
    telemetry.active = value;
} else if (typeof value === 'number') {
    if (deviceId === 'light-main') {
        attributes = { brightness: value };
        telemetry.brightness = value;
    } else if (deviceId === 'ac') {
        attributes = { temperature: value };
        telemetry.temperature = value;
    } else if (deviceId === 'curtain') {
        attributes = { openPercentage: value };
        telemetry.openPercentage = value;
    } else if (deviceId === 'desk') {
        attributes = { height: value };
        telemetry.height = value;
    } else {
        attributes = { value: value };
        telemetry.value = value;
    }
    attributes.active = true;
} else {
    attributes = { value: value, active: true };
    telemetry.value = value;
}

console.log('========== UPDATE VALUE ==========');
console.log('[PUT /value] Device ID:', deviceId);
console.log('[PUT /value] Value:', value);

const httpMsg = { payload: { id: deviceId, value: value } };
const mqttMsg = {
    payload: { deviceId: deviceId, value: value },
    topic: 'iot/room/devices'
};

const tbAttrMsg = {
    url: `http://thingsboard:9090/api/v1/${deviceToken}/attributes`,
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    payload: attributes,
    deviceToken: deviceToken
};

const tbTelMsg = {
    url: `http://thingsboard:9090/api/v1/${deviceToken}/telemetry`,
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    payload: telemetry,
    deviceToken: deviceToken
};

console.log('[PUT /value] → MQTT Frontend:', JSON.stringify(mqttMsg.payload));
console.log('[PUT /value] → ThingsBoard Attributes:', tbAttrMsg.url);
console.log('[PUT /value] =================================');

return [httpMsg, mqttMsg, tbAttrMsg, tbTelMsg];

const updates = msg.payload;
const deviceTokens = context.global.get('deviceTokens');

if (!Array.isArray(updates) || updates.length === 0) {
    console.error('[POST /batch] ERROR: Invalid updates');
    msg.statusCode = 400;
    msg.payload = { message: 'Invalid updates' };
    return msg;
}

console.log('========== BATCH UPDATE ==========');
console.log('[POST /batch] Updates:', updates.length, 'devices');

const httpMsg = { payload: { success: true, updated: updates.length } };
const mqttMsg = {
    payload: updates,
    topic: 'iot/room/devices'
};

const tbMessages = updates.map(update => {
    const deviceToken = deviceTokens ? deviceTokens[update.id] : null;
    if (!deviceToken) {
        console.error('[POST /batch] Unknown device:', update.id);
        return null;
    }

    let attributes = {};
    if (update.isOn !== undefined) {
        attributes = { isOn: update.isOn };
    } else if (update.value !== undefined) {
        const deviceId = update.id;
        const value = update.value;
        if (typeof value === 'number') {
            if (deviceId === 'light-main') {
                attributes = { brightness: value };
            } else if (deviceId === 'ac') {
                attributes = { temperature: value };
            } else if (deviceId === 'curtain') {
                attributes = { openPercentage: value };
            } else if (deviceId === 'desk') {
                attributes = { height: value };
            } else {
                attributes = { value: value };
            }
        } else {
            attributes = { value: value };
        }
    }

    return {
        url: `http://thingsboard:9090/api/v1/${deviceToken}/attributes`,
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        payload: attributes,
        deviceToken: deviceToken
    };
}).filter(m => m !== null);

console.log('[POST /batch] → MQTT Frontend:', JSON.stringify(mqttMsg.payload));
console.log('[POST /batch] → ThingsBoard:', tbMessages.length, 'requests');
console.log('[POST /batch] =================================');

return [httpMsg, mqttMsg, tbMessages];

// 确保payload是对象格式，Node-RED会自动序列化为JSON
if (typeof msg.payload === 'string') {
    try {
        msg.payload = JSON.parse(msg.payload);
    } catch (e) {
        console.error('[TB] Failed to parse payload string:', e);
        return null;
    }
}

// 处理数组类型的输入（来自 batch update）
if (Array.isArray(msg)) {
    // 如果是数组，直接返回数组，让后续节点逐个处理（如果有Split节点）
    // 或者我们可以返回多条消息
    // Node-RED Function 节点支持返回数组的数组来发送多条消息到同一个输出
    // 例如 return [[msg1, msg2, msg3]];
    // 但这里输入已经是单条消息（可能包含payload是数组）
    // 让我们检查一下上游是怎么传过来的
    // 如果上游是 [httpMsg, mqttMsg, tbMessages]
    // tbMessages 是数组
    // 那么这里 msg 就是 tbMessages 中的一个元素？
    // 不，如果上游返回 [msg1, msg2, [m1, m2]]，第三个输出收到的是数组 [m1, m2]
    // 所以我们需要把数组拆开
    return msg.map(m => m);
}

// 如果 msg 本身是数组（直接传入了数组）
if (Array.isArray(msg.payload) && msg.url === undefined) {
     // 这种情况不应该发生，除非逻辑错误
     return null;
}

// 确保URL和method存在
if (!msg.url || !msg.method) {
    console.error('[TB] Missing url or method');
    return null;
}

console.log('[TB] Request URL:', msg.url);
console.log('[TB] Request Method:', msg.method);
console.log('[TB] Request Payload:', JSON.stringify(msg.payload));

return msg;

if (msg.statusCode >= 200 && msg.statusCode < 300) {
    console.log('[TB] ✔ Success:', msg.statusCode, msg.payload);
} else {
    console.error('[TB] ✘ Error:', msg.statusCode, msg.payload);
}
return null;

// ========== ThingsBoard 属性变化回调 ==========
// Payload 格式: { "deviceId": "...", "attributes": { "isOn": true } }

const data = msg.payload;
console.log('========== TB ATTRIBUTES CALLBACK ==========');
console.log('[TB] Data:', JSON.stringify(data));

const deviceId = data.deviceId;
const attributes = data.attributes;

if (!deviceId || !attributes) {
    console.error('[TB] ERROR: Missing deviceId or attributes');
    return null;
}

const payload = { deviceId: deviceId };

if (attributes.isOn !== undefined) payload.isOn = attributes.isOn;
if (attributes.brightness !== undefined) payload.value = attributes.brightness;
if (attributes.temperature !== undefined) payload.value = attributes.temperature;
if (attributes.openPercentage !== undefined) payload.value = attributes.openPercentage;
if (attributes.height !== undefined) payload.value = attributes.height;
if (attributes.value !== undefined) payload.value = attributes.value;

const mqttMsg = {
    payload: payload,
    topic: 'iot/room/devices'
};

console.log('[TB] → Frontend:', JSON.stringify(mqttMsg.payload));
console.log('[TB] ======================================');

return mqttMsg;

const { name, file } = msg.payload || {};

if (!name || !file) {
    msg.statusCode = 400;
    msg.payload = { message: 'Missing name or file' };
    return msg;
}

msg.payload = {
    id: 'user_' + Date.now(),
    name: name,
    avatarUrl: file.startsWith('data:') ? file : `data:image/jpeg;base64,${file}`,
    registeredAt: Date.now()
};
return msg;

const { file } = msg.payload || {};

if (!file) {
    msg.statusCode = 400;
    msg.payload = { message: 'Missing file' };
    return msg;
}

msg.statusCode = 404;
msg.payload = { message: 'User not recognized' };
return msg;

const fileContent = msg.payload;
let deviceTokensList = [];
try {
    deviceTokensList = JSON.parse(fileContent);
} catch (e) {
    console.error('[INIT] Failed to parse tokens file:', e);
    return null;
}

const deviceTokens = {};
deviceTokensList.forEach(dt => {
    deviceTokens[dt.label] = dt.accessToken;
});

const tokenToDeviceId = {};
Object.entries(deviceTokens).forEach(([id, token]) => {
    tokenToDeviceId[token] = id;
});

context.global.set('deviceTokens', deviceTokens);
context.global.set('tokenToDeviceId', tokenToDeviceId);

console.log('[INIT] Device tokens initialized from file, devices:', Object.keys(deviceTokens).length);

return msg;
